mapper.select:
SELECT * FROM metrics_job_data

	id = #{id}
	job_id = #{jobId}
	AND target_server = #{targetServer}
	AND metric_name_id = #{metricNameId}
	AND tenant_id = #{tenantId}

 ORDER BY id ASC

mapper.delete:
DELETE FROM metrics_job_data WHERE

	id = #{id}
	job_id = #{jobId}
	AND target_server = #{targetServer}
	AND metric_name_id = #{metricNameId}
	AND tenant_id = #{tenantId}

mapper.update:
UPDATE metrics_job_data
target_server = #{targetServer}, metric_name_id=#{metricNameId}, metric_interval=#{metricInterval}, metric_data_count=#{metricDataCount}, metric_value_lowest=#{metricValueLowest}, metric_value_highest=#{metricValueHighest}, metric_data_limit=#{metricDataLimit}, metric_time_limit=#{metricTimeLimit}, validate_result=#{validateResult}, job_id = #{jobId}, tenant_id = #{tenantId}, initial_setup_done = #{initialSetupDone},
WHERE id=#{id}
